import React  from "react";

import Masonry, {ReponsiveMasonry} from 'react-responsive-masonry'
import galleryImages from "./galleryImages";

const MasonryImagesGallery = () => {
    return (
        <ReponsiveMasonry columsCountBreakPoints = {{350:1, 768:3, 992:4}}>
            <Masonry gutter="1rem">
                {galleryImages.map((item, index) => (
                        <img src={item} key={index} alt="" style=
                        {{width : "100%", display : "block", borderRadius: "10px" }}
                        />
                    ))}
            </Masonry>
        </ReponsiveMasonry>
    );
}

export default MasonryImagesGallery;
